import React, { useState } from 'react'
import Header from "./Header";
import {Table} from 'react-bootstrap';


function Home() {
  const [data, setData] = useState("")

  function Fetch(){
    fetch("https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json")
      .then(Response => {
        return Response.json()
      }).then(data => {
        setData(data)
      })
  }
  console.log(data)
  return (
    <div>
      <Header />
      <h3>Home</h3>
      <h1>User Data</h1>
      <Table striped bordered hover variant="light">
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {/* <tr></tr> */}
          {props.list.map((item, i) => {
            return (
              <>
                <tr>
                  <td> {item.id}</td>
                  <td>{item.first_name}</td>
                  <td>{item.last_name}</td>
                  <td>{item.email}</td>
                </tr>
              </>
            );
          })}
        </tbody>
      </Table>
      <button variant="primary" onClick={Fetch} type="submit">GetData</button>
    </div>
  )
}

export default Home
