import React, { useState } from 'react'
import Header from "./Header";
import {Table, Button} from 'react-bootstrap';


function Home() {
  const [data, setData] = useState("")

  function Fetch(){
    fetch("https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json")
      .then(Response => {
        return Response.json()
      }).then(data => {
        setData(data)
        console.log(data)
      })
  }

  return (

    <div>
      <Header />
      <h3>Home</h3>
      <div className='container'>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Phone Number</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item)=>{
            return(
              <>
              <tr>
                <td>{item.firstname}</td>
                <td>{item.lastname}</td>
                <td>{item.phonebu}</td>
              </tr>
              </>
            )
          })}
{/*          
          {data.map((item, i) => {
            return (
              <>
                <tr>
                  <td> {item.firstname}</td>
                  <td>{item.lastname}</td>
                  <td>{item.phonenumber}</td>
                  <td>{item.email}</td>
                </tr>
              </>
            );
          })} */}
        </tbody>
      </Table>
      <br>
      </br>
      <Button variant="primary" onClick={Fetch} type="submit">GetData</Button>
      </div>

    </div>

  )
}

export default Home
