import React, { useEffect, useState } from "react";
import Header from "./Header";

function Send() {
  const [inputValues, setInputValue] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phonenumber: "",
  });

  const [validation, setValidation] = useState({
    fName: "",
    lName: "",
    email: "",
    phonenumber: "",
  });

  //handle submit updates
  function handleChange(event) {
    const { name, value } = event.target;
    setInputValue({ ...inputValues, [name]: value });
  }

  const checkValidation = () => {
    let errors = validation;

    //first Name validation
    const reg = /^[a-zA-Z\s]+$/;
    if (!inputValues.fName.trim()) {
      errors.fName = "First name is required";
    } else if (!inputValues.fName.match(reg)) {
      errors.fName = "Please enter a valid first name";
    } else {
      errors.fName = "";
    }

    //last Name validation
    const regs = /^[a-zA-Z\s]+$/;
    if (!inputValues.lName.trim()) {
      errors.lName = "Last name is required";
    } else if (!inputValues.lName.match(regs)) {
      errors.lName = "Please enter a valid last name";
    } else {
      errors.lName = "";
    }

    // email validation
    const emailCond = '^[a-zA-Z0-9._:$!%-]+@[a-zA-Z0-9.-]+.[a-zA-Z]$';
    if (!inputValues.email.trim()) {
      errors.email = "Email is required";
    } else if (!inputValues.email.match(emailCond)) {
      errors.email = "Please enter a valid email address";
    } else {
      errors.email = "";
    }

    //Phone number validations
    const regex = "^[1-9]{10}";
    if (!inputValues.phonenumber.trim()) {
      errors.phonenumber = "Phone number is required";
    } else if (!inputValues.phonenumber.match(regex)) {
      errors.phonenumber = "Enter a valid phone number";
    }else {
      errors.phonenumber = "";
    }

    setValidation(errors);
  };

  useEffect(() => {
    checkValidation();
  }, [inputValues]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // console.log(inputValues);
    if (checkValidation) {
      console.log("jnvhjde", inputValues);
        fetch(
          "https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json",
          {
            method: "POST",
            body: JSON.stringify(inputValues),
            headers: {
              "Content-Type": "application/json",
            },
            mode:'cors',
          }
        ).then((res) => {
          console.log(res);
        });
    }
  };

  return (
    <div>
      <Header />
      <br></br>
      <br></br>
      <div className="container">
        <form
          id="registrationForm"
        //   action="/"
        //   method="POST"
        //   onSubmit={handleSubmit}
        >
          <div className="form-control">
            <input
              placeholder="First Name"
              type="string"
              name="fName"
              id="fName"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.fName}
            />
            {validation.fName && <p>{validation.fName}</p>}
          </div>
          <div className="form-control">
            <input
              placeholder="Last Name"
              type="string"
              id="lName"
              name="lName"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.lName}
            />
            {validation.lName && <p>{validation.lName}</p>}
          </div>
          <div className="form-control">
            <input
              placeholder="email"
              type="email"
              name="email"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.email}
            />
          </div>
          {validation.email && <p>{validation.email}</p>}

          <div className="form-control">
            <input
              placeholder="phone number"
              type="number"
              name="phonenumber"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.phonenumber}
            />
          </div>
          {validation.phonenumber && <p>{validation.phonenumber}</p>}
          <br></br>
          <button type="submit" id="submit-button" onClick={handleSubmit}>
            submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default Send;
