import React from 'react'
import { useEffect, useRef } from 'react';

function Send() {


  return (
    <div>
            <form>
        <input ref={el => inputRef.current['firstname'] = el} placeholder='First Name'></input>
        <input ref={el => inputRef.current['lastname'] = el} placeholder='Last Name'></input>
        <input ref={el => inputRef.current['email'] = el}  placeholder='Email'></input>
        <input ref={el => inputRef.current['phonenumber'] = el}  placeholder='Phone number'></input>
        <button type='submit' onClick={inputRef.current.value}>Add Data</button>
      </form>
    </div>
  )
}

export default Send
