// import { Button } from "bootstrap";
import React from "react";
import Header from "./Header";
import { Button, Form } from 'react-bootstrap';
 import useState from 'react'

function Contact() {
  console.log("jfefnkjenfkjef");
  const [email, setemail] = useState("");
  const [fname, setfname] = useState("");
  const [lname, setlname] = useState("");
  const [phonenumber, setphonenumber] = useState("");

  const handleFname = (e) => {
    setfname({fname: e.target.value });
    console.log("dfjkffdg",fname)
  };

  const handleLname = (e) => {
    setlname({lname: e.target.value });
    console.log("dfjkffdg",lname)
  };

  const handlePhone = (e) => {
    setphonenumber({phonenumber: e.target.value });
    console.log("dfjkffdg",phonenumber)
  };

  const handleEmail = (e) => {
    setemail({ email: e.target.value });
    console.log("dfdg",email)
  };

  function sendHandler() {

    fetch("https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    }).then((res) => {
      console.log(res);
    });
  }
  return (
    <div>
      <Header />

        {/* <form >
          <label htmlFor="fname">First Name</label>
          <input
            type="text"
            id="fname"
            className="firstname"
            placeholder="Your first name.."
          />

          <label htmlFor="lname">Last Name</label>
          <input
            type="text"
            id="lname"
            className="lastname"
            placeholder="Your last name.."
          />

          <label htmlFor="phonenumber">Phone Number</label>
          <input
            type="text"
            id="phonenumber"
            className="phonenumber"
            placeholder="Your phone number.."
          />

          <label htmlFor="email">Email</label>
          <input
            type="text"
            id="email"
            className="email"
            placeholder="Your email.."
          />

          <Button type="submit" value="Submit"></Button>
        </form> */}

       <Form>
      <FormGroup
          className="mb-3"
          value={fname}
          onChange={handleFname}
        >
          <Form.Label>First Name</Form.Label>
          <Form.Control type="text" placeholder="First Name" />
        </FormGroup>
      <Form.Group
          className="mb-3"
          value={lname}
          onChange={handleLname}
        >
          <Form.Label>Last Name</Form.Label>
          <Form.Control type="text" placeholder="Last Name" />
        </Form.Group>
        <Form.Group
          className="mb-3"
          value={email}
          onChange={handleEmail}
        >
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>
        <Form.Group
          className="mb-3"
          value={phonenumber}
          onChange={handlePhone}
        >
          <Form.Label>Phone Number</Form.Label>
          <Form.Control type="number" max={10} min={10} placeholder="Password" />
        </Form.Group>
      </Form> 
      <Button onClick={sendHandler}>Send Data</Button>
    </div>
  );
}

export default Contact;
