      "lastname": "Digant",
      "phonenumber": "9999999999",
      "email": "asd@zxc.com"
    }

    fetch("https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json", {
      method: "POST",
      body: JSON.stringify(value),
      headers: {
        "Content-Type": "application/json",
      },

    }).then((res) => {
      console.log(res);
