import React, { useEffect, useState } from "react";
import Header from './Header'

function Send() {
  const [inputValues, setInputValue] = useState({
    fName: "",
    lName: "",
    email: "",
    phonenumber: "",

  });

  const [validation, setValidation] = useState({
    fName: "",
    lName: "",
    email: "",
    phonenumber: "",
  });

  //handle submit updates
  function handleChange(event) {
    const { name, value } = event.target;
    setInputValue({ ...inputValues, [name]: value });
  }

  const checkValidation = () => {
    let errors = validation;

    //first Name validation
    const reg = /^[a-zA-Z\s]+$/;
    if (!inputValues.fName.trim()) {
      errors.fName = "First name is required";
    } else if (!inputValues.email.match(emailCond)) {
        errors.email = "Please enter a valid email address";
      }else {
      errors.fName = "";
    }

    //last Name validation
    if (!inputValues.lName.trim()) {
      errors.lName = "Last name is required";
    } else {
      errors.lName = "";
    }

    // email validation
    const emailCond =
      "/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:.[a-zA-Z0-9-]+)*$/";
    if (!inputValues.email.trim()) {
      errors.email = "Email is required";
    } else if (!inputValues.email.match(emailCond)) {
      errors.email = "Please enter a valid email address";
    } else {
      errors.email = "";
    }

      //Phone number validations
      const regex = /^[1-9]\d{9}$/;
      if (!inputValues.phonenumber.trim()) {
        errors.phonenumber = "Phone number is required";
      }  else if (!inputValues.phonenumber.match(regex)){
        errors.phonenumber = "Enter a valid phone number with 10 digit limit";
      } else {
        errors.phonenumber = "";
      }


    setValidation(errors);
  };

  useEffect(() => {
    checkValidation();
  }, [inputValues]);

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(inputValues)
  };

  return (
    <div>
        <Header />
        <br></br>
        <br></br>
      <div className="container">
        <form
          id="registrationForm"
          action="/"
          method="POST"
          onSubmit={handleSubmit}
        >
          <div className="form-control">
            <input
              placeholder="First Name"
              type="string"
              name="fName"
              id="fName"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.fName}
            />
            {validation.fName && <p>{validation.fName}</p>}
            {validation.fName && console.log(validation)}
          </div>
          <div className="form-control">
            <input
              placeholder="Last Name"
              type="string"
              id="lName"
              name="lName"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.lName}
            />
            {validation.lName && <p>{validation.lName}</p>}
          </div>
          <div className="form-control">
            <input
              placeholder="email"
              type="email"
              name="email"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.email}
            />
          </div>
          {validation.email && <p>{validation.email}</p>}

          <div className="form-control">
            <input
              placeholder="phone number"
              type="number"
              name="phonenumber"
              className="input-field"
              onChange={(e) => handleChange(e)}
              value={inputValues.phonenumber}
            />
          </div>
          {validation.phonenumber && <p>{validation.phonenumber}</p>}

          <button type="submit" id="submit-button">
            submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default Send;
