// import { Button } from "bootstrap";
import React from "react";
import Header from "./Header";
import { Button, Form } from 'react-bootstrap';
import {useState} from 'react'

function Contact() {
  console.log("jfefnkjenfkjef");
  const [email, setemail] = useState("");
  const [fname, setfname] = useState("");
  const [lname, setlname] = useState("");
  const [phonenumber, setphonenumber] = useState("");

  const handleFname = (e) => {



    if (e.target.value == "") {
      printError("nameError", "*Please enter your name");
      var elem = document.getElementById("name");
      elem.classList.add("input-2");
      elem.classList.remove("input-1");

  }else if (name.length <5) {
      printError("nameError", "*name must be atleast 4 character");
      var elem = document.getElementById("name");
      elem.classList.add("input-2"); 
      elem.classList.remove("input-1");
  }else {
      var regex = /^[a-zA-Z\s]+$/;
      if (regex.test(name) === false) {
          printError("nameError", "*Please enter a valid name");
          var elem = document.getElementById("name");
          elem.classList.add("input-2");
          elem.classList.remove("input-1");

      }else {
          printError("nameError", "");
          nameError = false;
          var elem = document.getElementById("name");
          elem.classList.add("input-1");
      elem.classList.remove("input-2");


      }
  }

    setfname({fname: e.target.value });
    console.log("dfjkffdg",fname)
  };

  const handleLname = (e) => {
    setlname({lname: e.target.value });
    console.log("dfjkffdg",lname)
  };

  const handlePhone = (e) => {
    setphonenumber({phonenumber: e.target.value });
    console.log("dfjkffdg",phonenumber)
  };

  const handleEmail = (e) => {
    setemail({ email: e.target.value });
    console.log("dfdg",email)
  };

  function sendHandler() {

    fetch("https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    }).then((res) => {
      console.log(res);
    });
  }
  return (
    <div>
      <Header />
      <br>
      </br>
      <br></br>
      <br></br>
      <div className="container">
       <Form >
      <Form.Group
          className="mb-3"
          type="text"
          value={fname}
          onChange={handleFname}
        >
          <Form.Label>First Name</Form.Label>
          <Form.Control type="text" placeholder="First Name" />
        </Form.Group>
      <Form.Group
          className="mb-3"
          type="text"
          value={lname}
          onChange={handleLname}
        >
          <Form.Label>Last Name</Form.Label>
          <Form.Control type="text" placeholder="Last Name" />
        </Form.Group>
        <Form.Group
          className="mb-3"
          type="email"
          value={email}
          onChange={handleEmail}
        >
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>
        <Form.Group
          className="mb-3"
          value={phonenumber}
          onChange={handlePhone}
          type="number"
          maxLength={10}
        >
          <Form.Label>Phone Number</Form.Label>
          <Form.Control type="number" max={10} min={10} placeholder="Password" />
        </Form.Group>
      </Form> 
      <Button onClick={sendHandler}>Send Data</Button>
      </div>
    </div>
  );
}

export default Contact;
