import React from 'react'
import {useRef, useState} from 'react'
import Header from './Header'

function Send() {

  const firstname = useRef(null);
  const lastname = useRef(null);
  const email = useRef(null);
  const phonenumber = useRef(null);

  const [validation, setValidation] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phonenumber: "",
  });



  const checkValidation = () => {
    let errors = validation;

    //first Name validation
    const reg = /^[a-zA-Z\s]+$/;
    if (!inputValues.firstname.trim()) {
      errors.firstname = "First name is required";
    } else if (!inputValues.firstname.match(reg)) {
      errors.firstname = "Please enter a valid first name";
    } else {
      errors.firstname = "";
    }

    //last Name validation
    const regs = /^[a-zA-Z\s]+$/;
    if (!inputValues.lastname.trim()) {
      errors.lastname = "Last name is required";
    } else if (!inputValues.lastname.match(regs)) {
      errors.lastname = "Please enter a valid last name";
    } else {
      errors.lastname = "";
    }

    // email validation
    const emailCond = '^[a-zA-Z0-9._:$!%-]+@[a-zA-Z0-9.-]+.[a-zA-Z]$';
    if (!inputValues.email.trim()) {
      errors.email = "Email is required";
    } else if (!inputValues.email.match(emailCond)) {
      errors.email = "Please enter a valid email address";
    } else {
      errors.email = "";
    }

    //Phone number validations
    const regex = "^[1-9]{10}";
    if (!inputValues.phonenumber.trim()) {
      errors.phonenumber = "Phone number is required";
    } else if (!inputValues.phonenumber.match(regex)) {
      errors.phonenumber = "Enter a valid phone number";
    }else {
      errors.phonenumber = "";
    }

    setValidation(errors);
  };



  function submitForm(e){
    e.preventDefault();

    console.log(firstname.current.value);
    console.log(lastname.current.value);
    console.log(email.current.value);
    console.log(phonenumber.current.value);
   }




  return (
    <div>
      <Header />
      <br></br>
      <br></br>
      <form className='container' style={{ padding: "20px 20px" }}>
        <div>
      <input
              placeholder="first name"
              type="text"
              name="firstname"
              className="input-field"
              // onChange={(e) => handleChange(e)}
              ref={firstname}
            />
            </div><br></br>
            <div>
        <input
              placeholder="last name"
              type="text"
              name="lastname"
              className="input-field"
              // onChange={(e) => handleChange(e)}
              ref={lastname}
            />
            </div><br></br>
            <div>
        <input
              placeholder="email"
              type="email"
              name="email"
              className="input-field"
              // onChange={(e) => handleChange(e)}
              ref={email}
            />
            </div><br></br>
      <input
              placeholder="phone number"
              type="number"
              name="phonenumber"
              className="input-field"
              // onChange={(e) => handleChange(e)}
              ref={phonenumber}
            />
            <br>
            </br>
            <br></br>
            <button type="submit" id="submit-button" onClick={submitForm}>
            Add Data
          </button>
      </form>

    </div>
  )
}

export default Send
