import React from 'react'
import {useRef, useState} from 'react'
import Header from './Header';
import {useNavigate} from 'react-router-dom'

function Send() {

  const firstname = useRef(null);
  const lastname = useRef(null);
  const email = useRef(null);
  const phonenumber = useRef(null);

  const [validation, setValidation] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phonenumber: "",
  });



  const checkValidation = () => {
    let errors = validation;

    //first Name validation
    const reg = /^[a-zA-Z\s]+$/;
    if (!firstname.current.trim()) {
      errors.firstname = "First name is required";
    } else if (!firstname.current.match(reg)) {
      errors.firstname = "Please enter a valid first name";
    } else {
      errors.firstname = "";
    }

    //last Name validation
    const regs = /^[a-zA-Z\s]+$/;
    if (!lastname.current.value.trim()) {
      errors.lastname = "Last name is required";
    } else if (!lastname.current.match(regs)) {
      errors.lastname = "Please enter a valid last name";
    } else {
      errors.lastname = "";
    }

    // email validation
    const emailCond = '^[a-zA-Z0-9._:$!%-]+@[a-zA-Z0-9.-]+.[a-zA-Z]$';
    if (!email.current.value.trim()) {
      errors.email = "Email is required";
    } else if (!email.current.match(emailCond)) {
      errors.email = "Please enter a valid email address";
    } else {
      errors.email = "";
    }

    //Phone number validations
    const regex = "^[1-9]{10}";
    if (!phonenumber.current.value.trim()) {
      errors.phonenumber = "Phone number is required";
    } else if (!phonenumber.current.match(regex)) {
      errors.phonenumber = "Enter a valid phone number";
    }else {
      errors.phonenumber = "";
    }

    setValidation(errors);
  };


  const navigate = useNavigate();

  function submitForm(e){
    e.preventDefault();

    // console.log(firstname.current.value);
    // console.log(lastname.current.value);
    // console.log(email.current.value);
    // console.log(phonenumber.current.value);

    if (checkValidation) {
      // console.log(firstname.current.value);
      // console.log(lastname.current.value);
      // console.log(email.current.value);
      // console.log(phonenumber.current.value);
      let input={"firstname":firstname.current.value,
      "lastname":lastname.current.value, 
      "email":email.current.value,
      "phonenumber": phonenumber.current.value}

      console.log(input);
        fetch(
          "https://react-training-mobcoder-default-rtdb.firebaseio.com/userdata.json",
          {
            method: "POST",
            body: JSON.stringify(input),
            headers: {
              "Content-Type": "application/json",
            },
            mode:'cors',
          }
        ).then((res) => {
          console.log(res);
          // navigate('/');
        });
    }
   }




  return (
    <div>
      <Header />
      <br></br>
      <br></br>
      <form className='container' style={{ padding: "20px 20px" }}>
        <div>
          First Name 
      <input
              placeholder="first name"
              type="text"
              name="firstname"
              className="input-field"
              ref={firstname}
            />
            {validation.firstname && <p>{validation.firstname}</p>}
            </div><br></br>
            <div>
        Last Name <input
              placeholder="last name"
              type="text"
              name="lastname"
              className="input-field"
              ref={lastname}
            />
            {validation.lastname && <p>{validation.lastname}</p>}
            </div><br></br>
            <div>
        Email<input
              placeholder="email"
              type="email"
              name="email"
              className="input-field"
              ref={email}
            />
            {validation.email && <p>{validation.email}</p>}
            </div><br></br>
     Phone Number <input
              placeholder="phone number"
              type="number"
              name="phonenumber"
              className="input-field"
              ref={phonenumber}
            />
            {validation.phonenumber && <p>{validation.phonenumber}</p>}
            <br>
            </br>
            <br></br>
            <button type="submit" id="submit-button" onClick={submitForm}>
            Add Data
          </button>
      </form>

    </div>
  )
}

export default Send
